mapper.select:
select

  distinct


from sys_role




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

mapper.insert:
insert into sys_role (id, principal_name, principal_tel, 
  role_name, parent_id, `level`, 
  seq, conf_status, remark, 
  create_time, update_time, is_delete
  )
values (#{id,jdbcType=INTEGER}, #{principalName,jdbcType=VARCHAR}, #{principalTel,jdbcType=VARCHAR}, 
  #{roleName,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, #{level,jdbcType=VARCHAR}, 
  #{seq,jdbcType=INTEGER}, #{confStatus,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=VARCHAR}
  )
